package MVC;

import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.HashMap;
import java.util.Map;

public class UserForm {
	private String username;
	private String password;
	private String repassword;
	private String email;
	private String birthday;
	Map<String,String> msg = new HashMap<String,String>();
	
	public boolean validate() {
		// TODO Auto-generated method stub
		if("".equals(username)){
			msg.put("username", "用户名不能为空");
		}
		else if(!username.matches("\\w{3,8}")){//用户名：必须输入，且3~8位的字母组成<br>
			msg.put(username, "用户名为3~8位的字母组成");
			
		}
		
		if("".equals(password)){
			msg.put("password", "密码不能为空");
		}
		else if(!password.matches("\\d{3,8}")){//密码：必须输入，且3~8位的数字组成<br>
			msg.put(password, "密码为3~8位的字母组成");
			
		}
		
		//确认密码：和密码保持一致
		if(!repassword.equals(password)){
			msg.put("repassword", "两次密码不一致");
		}
		
		if("".equals(email)){
			msg.put("email", "邮箱不能为空");
		}
		
		if("".equals(birthday)){
			msg.put("birthday", "生日不能为空");
		}
		else{
			SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
			try {
				sdf.parse(birthday);
			} catch (ParseException e) {
				// TODO Auto-generated catch block
				msg.put("birthday", "生日格式不正确");
				e.printStackTrace();
			}
		}
		
		return msg.isEmpty();//当Map集合中没有数据时，返回true
	}
	public String getUsername() {
		return username;
	}
	public void setUsername(String username) {
		this.username = username;
	}
	public String getPassword() {
		return password;
	}
	public void setPassword(String password) {
		this.password = password;
	}
	public String getEmail() {
		return email;
	}
	public void setEmail(String email) {
		this.email = email;
	}
	
	
	public String getBirthday() {
		return birthday;
	}
	public void setBirthday(String birthday) {
		this.birthday = birthday;
	}
	public Map<String, String> getMsg() {
		return msg;
	}
	public void setMsg(Map<String, String> msg) {
		this.msg = msg;
	}
	
	
	public String getRepassword() {
		return repassword;
	}
	public void setRepassword(String repassword) {
		this.repassword = repassword;
	}
}
